import base64
import os

# 아이콘 이미지 (192x192)
icon_data = ''

# badge 이미지 (72x72)
badge_data = ''

# 디렉토리 생성 확인
icons_dir = os.path.join('public', 'icons')
if not os.path.exists(icons_dir):
    os.makedirs(icons_dir)

# 아이콘 이미지 저장
icon_path = os.path.join('public', 'icons', 'icon-192x192.png')
with open(icon_path, 'wb') as f:
    f.write(base64.b64decode(icon_data))
print('아이콘 이미지가 저장됨:', icon_path)

# 배지 이미지 저장
badge_path = os.path.join('public', 'icons', 'badge-72x72.png')
with open(badge_path, 'wb') as f:
    f.write(base64.b64decode(badge_data))
print('배지 이미지가 저장됨:', badge_path) 